mapper.update:
UPDATE dt_doctor
SET doctor_name = #{doctorName}
WHERE doctor_number = #{doctorNumber}

mapper.select:
SELECT DOCTOR_PW
FROM DT_DOCTOR
WHERE DOCTOR_ID = #{doctorId}
AND DOCTOR_NAME = #{doctorName}

SELECT DOCTOR_NAME , DOCTOR_ID
FROM DT_DOCTOR
WHERE DOCTOR_PHONE = #{doctorPhone}
