mapper.delete:
DELETE FROM DT_DOCTOR_COMMENT
WHERE MEDICAL_INFO_NUMBER = #{medicalInfoNumber}

DELETE FROM DT_MEDICAL_INFO
WHERE MEDICAL_INFO_NUMBER = #{medicalInfoNumber}

DELETE FROM DT_MEMBER
WHERE MEMBER_NUMBER IN (
SELECT MEMBER_NUMBER FROM DT_MEDICAL_INFO WHERE MEDICAL_INFO_NUMBER =
#{medicalInfoNumber}
)

DELETE FROM DT_DOCTOR
WHERE DOCTOR_NUMBER IN (
SELECT DOCTOR_NUMBER FROM DT_DOCTOR_COMMENT WHERE MEDICAL_INFO_NUMBER =
#{medicalInfoNumber}
)
